        PathBuilder.build(path)

        with open(path + "rep1.tsv", "w") as file:
            file.writelines(rep1text)

        with open(path + "rep2.tsv", "w") as file:
            file.writelines(rep2text)

        dataset = AdaptiveBiotechLoader.load(path, {
            "result_path": EnvironmentSettings.root_path + "test/tmp/adaptive/",
            "dataset_id": "emerson2017",
            "batch_size": 1,
            "region_type": "CDR3",  # can be loaded from metadata if available?
            "extension": "tsv"
        })

        self.assertEqual(2, dataset.get_repertoire_count())
        for index, rep in enumerate(dataset.get_data()):
            if index == 0:
                self.assertEqual("rep1", rep.identifier)
                self.assertEqual(8, len(rep.sequences))
            else:
                self.assertEqual("rep2", rep.identifier)
                self.assertEqual(6, len(rep.sequences))

        shutil.rmtree(path)
